//News Gallery with thumbnail//

$(document).ready(function() {
  var bigimage = $("#big");
  var thumbs = $("#thumbs");
  //var totalslides = 10;
  var syncedSecondary = true;

  bigimage
    .owlCarousel({
    items: 1,
    slideSpeed: 2000,
    nav: true,
    autoplay: true,
    dots: false,
    loop: true,
    responsiveRefreshRate: 200,
    navText: [
      '<i class="fa fa-chevron-left" aria-hidden="true"></i>',
      '<i class="fa fa-chevron-right" aria-hidden="true"></i>'
    ]
  })
    .on("changed.owl.carousel", syncPosition);

  thumbs
    .on("initialized.owl.carousel", function() {
    thumbs
      .find(".owl-item")
      .eq(0)
      .addClass("current");
  })
    .owlCarousel({
    items: 4,
    dots: false,
    nav: false,
    navText: [
      '<i class="fa fa-chevron-left" aria-hidden="true"></i>',
      '<i class="fa fa-chevron-right" aria-hidden="true"></i>'
    ],
    smartSpeed: 200,
    slideSpeed: 500,
    slideBy: 4,
    responsiveRefreshRate: 100
  })
    .on("changed.owl.carousel", syncPosition2);

  function syncPosition(el) {
    //if loop is set to false, then you have to uncomment the next line
    //var current = el.item.index;

    //to disable loop, comment this block
    var count = el.item.count - 1;
    var current = Math.round(el.item.index - el.item.count / 2 - 0.5);

    if (current < 0) {
      current = count;
    }
    if (current > count) {
      current = 0;
    }
    //to this
    thumbs
      .find(".owl-item")
      .removeClass("current")
      .eq(current)
      .addClass("current");
    var onscreen = thumbs.find(".owl-item.active").length - 1;
    var start = thumbs
    .find(".owl-item.active")
    .first()
    .index();
    var end = thumbs
    .find(".owl-item.active")
    .last()
    .index();

    if (current > end) {
      thumbs.data("owl.carousel").to(current, 100, true);
    }
    if (current < start) {
      thumbs.data("owl.carousel").to(current - onscreen, 100, true);
    }
  }

  function syncPosition2(el) {
    if (syncedSecondary) {
      var number = el.item.index;
      bigimage.data("owl.carousel").to(number, 100, true);
    }
  }

  thumbs.on("click", ".owl-item", function(e) {
    e.preventDefault();
    var number = $(this).index();
    bigimage.data("owl.carousel").to(number, 300, true);
  });
});
/********************************************************
					Search toggle
********************************************************/

// Search toggle
$('.search-toggle').click(function(e) {
	$('.search-drawer').attr('aria-expanded', ($('.search-drawer').attr('aria-expanded')=='false') ? 'true':'false').slideToggle(200);
	$('.search-toggle span').toggle();
	$('.big-search .search-input').focus();
	
	$('body').toggleClass('searchopened');
  
	return false;
});


// Close the search drawer
function closeDrawer() {
	$('.search-drawer').attr('aria-expanded','false').slideUp(200);
	$('.search-toggle span').hide();
	$('.search-toggle span:first-child').show();
}


// When search drawer is open, allow click on body to close
$('html').click(function() {
	closeDrawer();
  $('body').removeClass('searchopened');

});


// Escape key closes drawer
$(document).keyup(function(e) {
	if (e.keyCode == 27) {
	  $('body').removeClass('searchopened');
		// Hide any drawers that are open
		closeDrawer();
		closeLightbox();
	}
});


// Allow clicking in the drawer when it's open
$('.search-drawer').click(function(event){
	event.stopPropagation();
});



/********************************************************
					Fix Navbar at top
********************************************************/


$(window).scroll(function() {
  if ($(document).scrollTop() > 40) {
    $('header').addClass('stuck');
  } else {
    $('header').removeClass('stuck');
  }
});

