$(document).ready(function() {
    $('.nav .dropdown').hover(function() {
       $(this).addClass('open');
    }, function() {
       $(this).removeClass('open');
    });
  
    //JS for tab nav
   $(".nav > li").click(function(){
      $(this).addClass("active");
          $(this).siblings().removeClass("active");
  });
});

