/********************************************************
						datetimepicker
********************************************************/
$(function () {
	$('#datetimepicker').datetimepicker({
		inline: true,
		language: 'ar',
		sideBySide: true,
		weekStart: 1,
		//todayBtn:  'linked',
		autoclose: 1,
		todayHighlight: 1,
		startView: 2,
		minView: 2,
		forceParse: 0,
		leftArrow: '<i class="fa fa-long-arrow-left"></i>',
		rightArrow: '<i class="fa fa-long-arrow-right"></i>'

	});

});

$('#btnDateNews').click(function () {
   
		  var date = $("#datetimepicker").data("datetimepicker").getDate(),
	 formatted =date.getDate() + "-" +(date.getMonth() + 1) + "-" +   date.getFullYear() ;
		  window.location.href = "/all_news?date=" + formatted;
});

$('#btnDateAnnouncements').click(function () {

    var date = $("#datetimepicker").data("datetimepicker").getDate(),
        formatted = date.getDate() + "-" + (date.getMonth() + 1) + "-" + date.getFullYear();
    window.location.href = "/announcements?date=" + formatted;
});
$('#btnDateEvents').click(function () {

    var date = $("#datetimepicker").data("datetimepicker").getDate(),
        formatted = date.getDate() + "-" + (date.getMonth() + 1) + "-" + date.getFullYear();
    window.location.href = "/Events?date=" + formatted;
});

/********************************************************
					Accordion Menu
********************************************************/


(function($){
$(document).ready(function(){

$('.acc-menu li.active').addClass('open').children('ul').show();
	$('.acc-menu li.has-sub>a').on('click', function(){
		$(this).removeAttr('href');
		var element = $(this).parent('li');
		if (element.hasClass('open')) {
			element.removeClass('open');
			element.find('li').removeClass('open');
			element.find('ul').slideUp(200);
		}
		else {
			element.addClass('open');
			element.children('ul').slideDown(200);
			element.siblings('li').children('ul').slideUp(200);
			element.siblings('li').removeClass('open');
			element.siblings('li').find('li').removeClass('open');
			element.siblings('li').find('ul').slideUp(200);
		}
	});

});
})(jQuery);

/********************************************************
					Move Menu
********************************************************/

$( window ).width(function() {
if ($(window).width() < 986) {
  $('.moveit').appendTo('#mainnavbar');
} else {
$('.moveit').appendTo('#mininavbar');
}

});

$( window ).resize(function() {
if ($(window).width() < 986) {
  $('.moveit').appendTo('#mainnavbar');
} else {
$('.moveit').appendTo('#mininavbar');
}
});	

/********************************************************
		swap between list view and grid view
********************************************************/

listButton = $('button.list-view');
gridButton = $('button.grid-view');
wrapper = $('.listwrapper');


listButton.on('click',function(){
	
  gridButton.removeClass('on');
  listButton.addClass('on');
  wrapper.removeClass('grid').addClass('list');
  
});

gridButton.on('click',function(){
	
  listButton.removeClass('on');
  gridButton.addClass('on');
  wrapper.removeClass('list').addClass('grid');
  
});


$(document).ready(function(){
    $(".preloader").fadeOut();
  })